import React, { useState } from 'react';
import style from './styles/index.module.scss';
import MenuDesplegable from '../components/MenuDesplegable';

const arrayMarcas = [];

function HomePage() {
  const [marcas, setMarcas] = useState([]);

  const handleSubmitMarca = (e) => {
    arrayMarcas.push(e.target.marca.value);
    setMarcas(arrayMarcas);
    e.preventDefault();
  };

  const handleSubmitItem = () => {

  };

  return (
    <div className={style.container}>
      <form onSubmit={(e) => handleSubmitMarca(e)}>
        <input type="text" name="marca" placeholder="Marca: " id="marca" />
        <button type="submit">Agregar marca</button>
      </form>
      <form onSubmit={(e) => handleSubmitItem(e)}>
        <nav id="menu">
          <ul>
            {
              arrayMarcas.length > 0
                ? arrayMarcas.map((item, index) => (
                  // eslint-disable-next-line react/no-array-index-key
                  <li key={index}>
                    {item}
                  </li>
                ))
                : null
            }
          </ul>
        </nav>
        <input type="text" name="modelo" placeholder="Modelo: " id="modelo" />
        <input type="text" name="precio" placeholder="Precio: " id="precio" />
        <button type="submit">Agregar Modelo</button>
      </form>
    </div>
  );
}

export default HomePage;
